    // Color scale function
    const colorShades = {
        50: '#E3EDF3',
        100: '#C7DBE6',
        200: '#8FB6CD',
        300: '#5790B3',
        400: '#306E96',
        500: '#26547C',
        600: '#1E4262',
        700: '#163048',
        800: '#0E1E2E',
        900: '#070C15',
    };

    function getColor(numReports: number): string {
        if (numReports > 10) {
        return colorShades[500];
        } else if (numReports > 5) {
        return colorShades[300];
        } else if (numReports > 0) {
        return colorShades[100];
        } else {
        return colorShades[50];
        }
    }

  
    // Fetch reports data and update colors
    onMount(async () => {
        try {
        reportsData = await getReportsPerProvince();
        provinceColors = calculateProvinceColors(reportsData);
        } catch (error) {
        console.error('Failed to fetch reports data:', error);
        }
    });


    function calculateProvinceColors(data: { [key: string]: number }) {
        return Object.fromEntries(
        Object.entries(data).map(([province, count]) => [province, getColor(count)])
        );
    }

    function handleProvinceClick(provinceName: string) {
        selectedProvince = provinceName;
    }

    function handleProvinceHover(provinceName: string) {
        hoveredProvince = provinceName;
    }

    function handleProvinceMouseOut(provinceName: string) {
        if (hoveredProvince === provinceName) {
        hoveredProvince = null;
        }
    }

    function closePopup() {
        selectedProvince = null;
    }

    function handleMouseMove(event: MouseEvent) {
        mouseX = event.clientX + 10;
        mouseY = event.clientY + 10;
    }
  </script>
  
