        <g id="features">
            {#each provinces as province}
            <path
              d="{province.d}" 
              id="{province.id}"
              name="{province.name}"        
              on:click={() => handleProvinceClick(province.name)}
              on:mouseover={() => handleProvinceHover(province.name)}
              on:mouseout={() => handleProvinceMouseOut(province.name)}
              on:focus={() => handleProvinceHover(province.name)}
              on:blur={() => handleProvinceMouseOut(province.name)}
              tabindex="0"
              role="button"
              aria-label="{province.name}"
              class:selected={selectedProvince === province.name}
              class:hovered={hoveredProvince === province.name}
              style="fill: {provinceColors[province.id] || '#cccccc'}"
