  <style>
    .map-container {
      position: relative;
      width: 100%;
      max-width: 800px;
      height: auto;
    }
  
    path {
      fill: #ccc;
      stroke: #333;
      cursor: pointer;
      transition: fill 0.2s;
    }
  
    path.hovered {
      fill: #99ccff;
    }
  
    path.selected {
      fill: #3399ff;
    }
  
    .tooltip {
      position: absolute;
      background-color: #26547C;
      color: #fff;
      padding: 5px 8px;
      border-radius: 4px;
      pointer-events: none;
      font-size: 14px;
      z-index: 1000;
    }
  
    .popup {
      position: fixed;
      background-color: #fff;
      padding: 20px;
      border: 2px solid #333;
      border-radius: 8px;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      z-index: 2000;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    }
  
    .popup-close {
      position: absolute;
      top: 5px;
      right: 10px;
      background: transparent;
      border: none;
      font-size: 16px;
      cursor: pointer;
    }
  
    @media (max-width: 800px) {
      .map-container {
        width: 100%;
        height: auto;
      }
  
      svg {
        width: 100%;
        height: auto;
      }
    }
  </style>
  